import React, { Component } from 'react';
import './App.css';
import * as d3 from 'd3';
import data from './dataset/sachin.csv';
import BarChart from './BarChart';

class App extends Component {
    constructor(props) {
        super(props)
        this.state={};
        }

    componentDidMount() {
        let currentComponent = this;
        d3.csv(data).then(function(data) {
            var colbatt = data.map(function(d) {
                return {
                    col0: d.batting_score
                }
            });

            currentComponent.setState({data:colbatt})
        }).catch(function(err) {
            console.log(err)
        })
    }
    render() {
        const {data}=this.state;

        return (


            <svg width={500} height={500}>
                <g transform={`translate(${500 / 2}, ${500 / 2})`}>
                    {data.map(d => (
                        <g className="arc" key={`a${d.data.age}`}>
                            <path d={arc(d)} fill={color(d.data.age)} />
                            <text transform={`translate(${arc.centroid(d)})`} dy=".35em">
                                {d.data.age}
                            </text>
                        </g>
                    ))}
                </g>
            </svg>
        );
  }
}

export default App;
