            console.log(testArray)
            var testArray4=Object.entries(colbatt).slice(201,250).map(entry => entry[1].col0);
            console.log(testArray)
            var testArray5=Object.entries(colbatt).slice(251,299).map(entry => entry[1].col0);
            console.log(testArray)
            var testArray6=Object.entries(colbatt).slice(300,400).map(entry => entry[1].col0);
            console.log(testArray)







