            var test1=average(list)
*/


            currentComponent.setState({data:{
                    labels: Array(200).fill(2),
                    datasets:[
                        {
                            data:[1,1,2,1,2,1],
                            backgroundColor:[
                                'rgba(255, 99, 132, 0.6)',
                                'rgba(54, 162, 235, 0.6)',
                                'rgba(255, 206, 86, 0.6)',
                                'rgba(75, 192, 192, 0.6)',
                                'rgba(153, 102, 255, 0.6)',
                                'rgba(255, 159, 64, 0.6)',
                                'rgba(255, 99, 132, 0.6)'
                            ]
                        }
                    ]


                }})
        }).catch(function(err) {
            console.log(err)
        })
    }




    render() {
        const {data}=this.state;

        return (
            <div className="chart">
                <Line
                    data={data}
                    width={50}
                    height={500}
                    options={{ maintainAspectRatio: false }}
                />
            </div>
        );
    }
}
export default  Chart;
