import React, { Component } from 'react';
import './App.css';
//import * as d3 from 'd3';
import {csv} from 'd3-request';
import { scaleBand, scaleLinear } from 'd3-scale'
import data from './dataset/sachin.csv';


class App extends Component {
    constructor(props) {
        super(props)
        this.setState={};
        this.xScale = scaleBand()
        this.yScale = scaleLinear()
    }

   /* componentDidMount() {
    d3.csv(data).then(function(data) {
            console.log(data[0])
        }).catch(function(err) {
            this.setState({})
        })
    }*/
    componentWillMount() {
        csv(data, (error, data) => {
            if (error) {
                this.setState({loadError: true});
            }
            this.setState({
                data: data.map(d => ({...d, x: Number(d.batting_score), y: Number(d.wickets)}))
            });
        })
    }
    render() {
       /* if (this.state.loadError) {
            return <div>couldn't load file</div>;
        }
        if (!this.state.data) {
            return <div />;
        }*/
        /*return <div style={{
            background: '#fff',
            borderRadius: '3px',
            boxShadow: '0 1 2 0 rgba(0,0,0,0.1)',
            margin: 12,
            padding: 24,
            width: '350px'
        }}>
            <h1>Birth and death rates of selected countries</h1>
            <h2>per 1,000 inhabitants</h2>
            <Scatterplot data={this.state.data}/>
        </div>;*/
  }
}

export default App;
